import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

const CreateEmployee = () => {
    const [user, setUser] = useState({
        firstName: '',
        lastName: '',
        emailId: ''
    });
    const navigate = useNavigate();
    function saveform() {
        let employee = { firstName: user.firstName, lastName: user.lastName, emailId: user.emailId };
        console.log(JSON.stringify(employee));
    }
    function cancel() {
        navigate('/employees')
    }
    return (
        <div className='container'>
            <div className='row'>
                <div className='card col-md-6 offset-md-3 '>
                    <h3 className='text-center'>Add Employee</h3>
                    <div className='card-body'>
                        <form>
                            <div className='form-group'>
                                <label>First Name:</label>
                                <input name='firstname' placeholder='Enter First Name' onChange={(e) => setUser({ ...user, firstName: e.target.value })}></input>
                            </div>
                            <div className='form-group'>
                                <label>Last Name:</label>
                                <input name='lastname' placeholder='Enter Last Name' onChange={(e) => setUser({ ...user, lastName: e.target.value })}></input>
                            </div>
                            <div className='form-group'>
                                <label>Email:</label>
                                <input name='email' placeholder='Enter email' onChange={(e) => setUser({ ...user, emailId: e.target.value })}></input>
                            </div>
                            <button className='btn btn-success' onClick={() => saveform}>Save</button>
                            <button className='btn btn-danger' style={{ marginLeft: "10px" }} onClick={() => cancel}>cancel</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CreateEmployee
