import React, { useState } from 'react'
import { Button, Card, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const CreateEmployee = () => {
    const [user, setUser] = useState({
        firstName: '',
        lastName: '',
        emailId: ''
    });
    const navigate = useNavigate();
    function saveform() {
        let employee = { firstName: user.firstName, lastName: user.lastName, emailId: user.emailId };
        console.log(employee);
    }

    return (
        <div className='container'>
            <Card className='card col-md-6 offset-md-3 offset-md-3'>
                <Form>
                    <Form.Group className="mb-3" >
                        <Form.Label>First Name:</Form.Label>
                        <Form.Control type="text" placeholder="Enter email" onChange={() => setUser({ ...user, firstName: e.value.target })} />
                    </Form.Group>
                    <Form.Group className="mb-3" >
                        <Form.Label>last Name:</Form.Label>
                        <Form.Control type="text" placeholder="Enter Last Name" />
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Label>Email:</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" />
                    </Form.Group>
                    <div className='d-flex justify-content-between'>
                        <Button variant='success' onClick={() => saveform}>save</Button>
                        <Button variant='danger' onClick={() => navigate('/employees')}>Cancel</Button>
                    </div>
                </Form>
            </Card>
        </div>
    )
}

export default CreateEmployee
